import React, { useState } from 'react';
import './App.css';
import BookSearch from './BookSearch';
import ReadStatus from './ReadStatus';

function App() {
  const [showSearchPage, setShowSearchPage] = useState(false);

  return (
    <div className="app">
      {showSearchPage ? (
        <BookSearch onBack={() => setShowSearchPage(false)} />
      ) : (
        <ReadStatus onSearch={() => setShowSearchPage(true)} />
      )}
    </div>
  );
}

export default App;
