  //     );
  //     setBooks(updatedBooks);
  //   } catch (error) {
  //     console.error("Error updating book shelf: ", error);
  //   }
  // };

  return (
    <Routes>
      <Route
        exact
        path="/"
        element={
          <ReadStatus books={books} handleShelfChange={handleShelfChange} />
        }
      />
      <Route
        path="/search"
        element={
          <BookSearch handleShelfChange={handleShelfChange} shelves={shelves} />
        }
      />
    </Routes>
  );
};

export default App;

