function BookSearch() {
  const [searchResults, setSearchResults] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    BooksAPI.search(book, 10)
      .then((data) => {
        setSearchResults(data);
      })
      .catch((error) => {
        setError(error);
      });
