  if (searchResults.length === 0) {
    return <p>Loading...</p>;
  }

  if (error) {
    return <p>Error fetching data.</p>;
  }
  return (
    <div>
      <div className="search-books-bar">
        <div className="close-search-page">
          <Link to="/" className="close-search">
            close
