import { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import * as BooksAPI from "./BooksAPI";
import ReadStatus from "./ReadStatus";

const BookSearch = ({ books, setBooks }) => {
  const [searchQuery, setSearchQuery] = useState("");
  const [searchResult, setSearchResult] = useState([]);

  useEffect(() => {
    let isMounted = true; // Add a variable to track if the component is mounted

    const fetchSearchResults = async () => {
      if (searchQuery.trim() === "") {
        setSearchResult([]);
        return;
      }

      try {
        const searchResults = await BooksAPI.search(searchQuery, 10);
        if (isMounted) {
          setSearchResult(searchResults);
        }
      } catch (error) {
        console.error("Error searching books: ", error);
        if (isMounted) {
          setSearchResult([]);
        }
      }
    };

    fetchSearchResults();

    return () => {
      // Cleanup function to cancel any ongoing tasks or subscriptions
      // if the component is unmounted before the search is completed
      // or if the search query changes.
      isMounted = false;
    };
  }, [searchQuery]);

  const onSearchQueryChange = (event) => {
    const query = event.target.value;
    setSearchQuery(query);
  };

  return (
    <div>
      <div className="search-books-bar">
        <div className="close-search-page">
          <Link to="/" className="close-search">
            close
          </Link>
        </div>
        <div className="search-books-input-wrapper">
          <input
            type="text"
            placeholder="Search by title, author, or ISBN"
            value={searchQuery}
            onChange={onSearchQueryChange}
          />
        </div>
        <div className="search-books-results">
          <ol className="books-grid">
            {searchResult.map((book) => (
              <li key={book.id}>
                <div className="book">
                  <div className="book-top">{<ReadStatus />}</div>
                </div>
              </li>
            ))}
          </ol>
        </div>
      </div>
    </div>
  );
};

export default BookSearch;
